logger = logging.getLogger() router = APIRouter()
# Helper function to create a new user (reused by both signup and Google OAuth) async def _create_new_user( db: AsyncSession, user_in: UserCreate, send_verification_email: bool = True ) -> tuple[Users, str]: """ Internal helper to create a new user.
Returns: Tuple of (new_user, message) """ user_in.email = user_in.email.lower()
# Check if user already exists existing_user = await crud_user.get_by_email(db=db, email=user_in.email) if existing_user: raise ValueError("User with this email already exists")
# Hash password user_in.password = hash_password(user_in.password) new_user = await crud_user.create(db=db, obj_in=user_in)
# Create verification token verification_record = await create_verification_token( db=db, entity_id=new_user.id, verification_type=VerificationType.EMAIL, entity_type="user", expires_in_hours=24 )
if send_verification_email: # Send verification email try: user_name = f"{new_user.first_name} {new_user.last_name}" email_sent = email_service.send_verification_email( to_email=new_user.email, verification_token=verification_record.token, user_name=user_name ) except Exception as e: await db.rollback() logger.error(f"Error sending verification email: {str(e)}") email_sent = False
if not email_sent: raise Exception(f"Warning: Failed to send verification email to {new_user.email}")
message = "User created successfully. Please check your email to verify your account." else: # Auto-verify email for OAuth users await crud_user.verify_token(db=db, db_obj=verification_record) message = "User created successfully via Google OAuth"
return new_user, message
@router.post("/auth/signup", response_model=ResponseMessage, status_code=status.HTTP_201_CREATED) async def create_user( *, db: AsyncSession = Depends(get_db), response: Response, user_in: UserCreate ) -> ResponseMessage: """ Register a new user and send email verification. """
try: new_user, message = await _create_new_user(db, user_in, send_verification_email=True) response.status_code = status.HTTP_201_CREATED return ResponseMessage(message=message, status="success") except ValueError as e: response.status_code = status.HTTP_400_BAD_REQUEST return ResponseMessage(message=str(e), status="error") except Exception as e: await db.rollback() response.status_code = status.HTTP_500_INTERNAL_SERVER_ERROR return ResponseMessage(message=f"Internal server error: {str(e)}", status="error")
@router.post("/auth/verify-email", response_model=DataResponse) async def verify_email( *, db: AsyncSession = Depends(get_db), verification_in: VerificationRequest, response: Response ) -> DataResponse: """ Verify user email with token. """ try: token = await crud_user.get_verification_token( db=db, token=verification_in.token, type=VerificationType.EMAIL )
if not token: response.status_code = status.HTTP_404_NOT_FOUND return DataResponse.error_response( status_code=status.HTTP_404_NOT_FOUND, message="Verification token not found" )
if token.status != VerificationStatus.PENDING or token.expires_at < datetime.now(): response.status_code = status.HTTP_400_BAD_REQUEST return DataResponse.error_response( status_code=status.HTTP_400_BAD_REQUEST, message="Token has expired or is invalid" )
result = await crud_user.verify_token(db=db, db_obj=token) if result: return DataResponse.success_response( message="Email verified successfully" )
response.status_code = status.HTTP_400_BAD_REQUEST return DataResponse.error_response( message="Email verification failed", status_code=status.HTTP_400_BAD_REQUEST )
except Exception as e: response.status_code = status.HTTP_500_INTERNAL_SERVER_ERROR return DataResponse.error_response( message=f"Verification process failed: {str(e)}", status_code=status.HTTP_500_INTERNAL_SERVER_ERROR )
