# Try to get professional by mobile number first user = await crud_user.get_by_email(db, email=login_data.email)
if not user: raise HTTPException( status_code=status.HTTP_401_UNAUTHORIZED, detail="Invalid credentials" )
# Verify password if not verify_password(login_data.password, user.password): raise HTTPException( status_code=status.HTTP_401_UNAUTHORIZED, detail="Invalid credentials" ) company = await crud_user.get_company_by_user(db, user.id)
# Create token pair tokens = create_token_pair(user.id, user.email, actor="user", ver="1", company_id=str(company.company_id) if company else '')
response.set_cookie( key="refresh_token", value=tokens["refresh_token"], max_age=tokens['rt_expires_in'], httponly=True, secure=True, # only over HTTPS samesite="none" ) response.set_cookie( key="access_token", value=tokens["access_token"], max_age=tokens['at_expires_in'], httponly=True, secure=True, # only over HTTPS samesite="none" )
return DataResponse.success_response(data = TokenResponse(**tokens))
