@router.put("/time-offs/{time_off_id}", response_model=DataResponse[TimeOff], status_code=status.HTTP_200_OK) async def update_time_off( *, time_off_id: str, db: AsyncSession = Depends(get_db), time_off_in: TimeOffUpdate, response: Response, current_user: User = Depends(get_current_active_user) ) -> DataResponse: """ Update an existing time off period. """ try: # Get the time off by ID time_off = await crud_time_off.get(db=db, time_off_id=time_off_id) if not time_off: response.status_code = status.HTTP_404_NOT_FOUND return DataResponse.error_response( message="Time off not found", status_code=status.HTTP_404_NOT_FOUND )
# Check if the time off belongs to the current user if str(time_off.user_id) != str(current_user.id): response.status_code = status.HTTP_403_FORBIDDEN return DataResponse.error_response( message="You don't have permission to update this time off", status_code=status.HTTP_403_FORBIDDEN )
# Strip timezone info from input datetime fields since database expects naive datetime if time_off_in.start_date and hasattr(time_off_in.start_date, 'tzinfo') and time_off_in.start_date.tzinfo: time_off_in.start_date = time_off_in.start_date.replace(tzinfo=None) if time_off_in.end_date and hasattr(time_off_in.end_date, 'tzinfo') and time_off_in.end_date.tzinfo: time_off_in.end_date = time_off_in.end_date.replace(tzinfo=None)
# Determine the new start and end dates for overlap check start_date = time_off_in.start_date if time_off_in.start_date is not None else time_off.start_date end_date = time_off_in.end_date if time_off_in.end_date is not None else time_off.end_date
# Strip timezone info from dates since database expects naive datetime if start_date and hasattr(start_date, 'tzinfo') and start_date.tzinfo: start_date = start_date.replace(tzinfo=None) if end_date and hasattr(end_date, 'tzinfo') and end_date.tzinfo: end_date = end_date.replace(tzinfo=None)
# Check for overlapping time offs has_overlap = await crud_time_off.check_overlapping_time_offs( db=db, user_id=current_user.id, start_date=start_date, end_date=end_date, exclude_id=time_off_id )
if has_overlap: response.status_code = status.HTTP_400_BAD_REQUEST return DataResponse.error_response( message="The updated time off period overlaps with existing ones", status_code=status.HTTP_400_BAD_REQUEST )
# Update the time off updated_time_off = await crud_time_off.update( db=db, db_obj=time_off, obj_in=time_off_in )
return DataResponse.success_response( message="Time off updated successfully", data=updated_time_off, status_code=status.HTTP_200_OK ) except ValueError as e: response.status_code = status.HTTP_400_BAD_REQUEST return DataResponse.error_response( message=str(e), status_code=status.HTTP_400_BAD_REQUEST ) except Exception as e: response.status_code = status.HTTP_500_INTERNAL_SERVER_ERROR return DataResponse.error_response( message=f"Failed to update time off: {str(e)}", status_code=status.HTTP_500_INTERNAL_SERVER_ERROR )
