# Delete the time off deleted = await crud_time_off.delete(db=db, time_off_id=time_off_id) if not deleted: response.status_code = status.HTTP_500_INTERNAL_SERVER_ERROR return DataResponse.error_response( message="Failed to delete time off", status_code=status.HTTP_500_INTERNAL_SERVER_ERROR )
