@router.post("/auth/refresh-token", response_model=DataResponse[TokenResponse]) async def refresh_token( request: Request, response: Response, db: AsyncSession = Depends(get_db) ) -> DataResponse: """ Refresh access and refresh tokens using the refresh token cookie. """ from app.core.config import settings try: # Get the refresh token from the request cookies refresh_token = request.cookies.get("refresh_token") if not refresh_token: raise HTTPException( status_code=status.HTTP_401_UNAUTHORIZED, detail="Refresh token not found" )
# Verify and decode the refresh token payload = verify_token(refresh_token, "refresh") if payload is None: raise HTTPException( status_code=status.HTTP_401_UNAUTHORIZED, detail="Invalid refresh token" )
# Extract user data from payload user_id = payload.get("sub") email = payload.get("email") company_id = payload.get("company_id")
if not user_id or not email: raise HTTPException( status_code=status.HTTP_401_UNAUTHORIZED, detail="Invalid token payload" )
# Create new tokens new_tokens = create_token_pair( id=user_id, email=email, actor="user", ver="1", company_id=company_id )
# Determine cookie domain - use shared domain for production cookie_domain = ".salona.me" if "salona.me" in settings.API_URL else None is_production = "https://" in settings.API_URL
# Set new cookies response.set_cookie( key="refresh_token", value=new_tokens["refresh_token"], max_age=new_tokens['rt_expires_in'], httponly=True, secure=is_production, samesite="lax", domain=cookie_domain ) response.set_cookie( key="access_token", value=new_tokens["access_token"], max_age=new_tokens['at_expires_in'], httponly=True, secure=is_production, samesite="lax", domain=cookie_domain )
return DataResponse.success_response(data=TokenResponse(**new_tokens))
except HTTPException as e: raise e except Exception as e: raise HTTPException( status_code=status.HTTP_500_INTERNAL_SERVER_ERROR, detail=f"Failed to refresh token: {str(e)}" )
@router.post("/auth/verify-token", response_model=DataResponse[dict]) async def verify_access_token( request: Request, response: Response ) -> DataResponse: """ Verify the access token and return the token data. """ try: # Get the access token from the request cookies access_token = request.cookies.get("access_token") if not access_token: raise HTTPException( status_code=status.HTTP_401_UNAUTHORIZED, detail="Access token not found" )
# Verify and decode the access token payload = verify_token(access_token, "access") if payload is None: raise HTTPException( status_code=status.HTTP_401_UNAUTHORIZED, detail="Invalid or expired access token" )
# Return token validity info return DataResponse.success_response(data={ "valid": True, "user_id": payload.get("sub"), "email": payload.get("email"), "company_id": payload.get("company_id"), "expires_at": payload.get("exp") })
except HTTPException as e: raise e except Exception as e: raise HTTPException( status_code=status.HTTP_500_INTERNAL_SERVER_ERROR, detail=f"Failed to verify token: {str(e)}" )
@router.post("/auth/google/authorize", response_model=GoogleAuthorizationResponse) async def google_authorize( response: Response ) -> GoogleAuthorizationResponse: """ Initiate Google OAuth flow - returns authorization URL and state token. """ try: state = GoogleOAuthService.generate_state_token()
# Store state in response cookie for verification later response.set_cookie( key="google_oauth_state", value=state, httponly=True, secure=True, samesite="lax", max_age=600 # 10 minutes )
authorization_url = GoogleOAuthService.get_authorization_url(state)
return GoogleAuthorizationResponse( authorization_url=authorization_url, state=state ) except Exception as e: raise HTTPException( status_code=status.HTTP_500_INTERNAL_SERVER_ERROR, detail=f"Failed to initiate Google OAuth: {str(e)}" )
@router.get("/auth/google/callback", response_model=DataResponse[TokenResponse]) async def google_callback( request: Request, response: Response, state: str = Query(..., description="State token for CSRF protection"), code: str = Query(..., description="Authorization code from Google"), db: AsyncSession = Depends(get_db) ) -> DataResponse: """ Handle Google OAuth callback for both signup and login. - If user exists: authenticates and returns tokens - If user doesn't exist: creates new user with random password and returns tokens
This unified endpoint eliminates the need for separate signup/login paths. """ from app.core.config import settings try: error = None google_email = "" google_name = "Google User" # Initialize with default value # Verify state token for CSRF protection stored_state = request.cookies.get("google_oauth_state") if not stored_state or stored_state != state: error = 'Invalid state parameter' else: # Get redirect_uri from environment or default redirect_uri = getattr(settings, 'GOOGLE_REDIRECT_URI', 'http://localhost:8000/api/v1/users/auth/google/callback')
# Exchange authorization code for tokens token_response = GoogleOAuthService.exchange_code_for_token( code, redirect_uri ) print(f'Token response: {token_response}') if not token_response: error = 'Failed to exchange authorization code for tokens' else: access_token = token_response.get("access_token") if not access_token: error = 'No access token in response'
else: # Get user info from Google user_info = GoogleOAuthService.get_user_info(access_token)
if not user_info: error = 'Failed to retrieve user information from Google'
else: google_email = user_info.get("email", "").lower() google_name = user_info.get("name", "Google User")
if not google_email: error = 'Google account does not have an email'
if error: raise HTTPException( status_code=status.HTTP_401_UNAUTHORIZED, detail=error ) # Check if user already exists user = await crud_user.get_by_email(db=db, email=google_email)
if user: # User exists - authenticate them company = await crud_user.get_company_by_user(db, user.id) tokens = create_token_pair( user.id, user.email, actor="user", ver="1", company_id=str(company.company_id) if company else '' ) auth_message = "Logged in successfully via Google" else: # User doesn't exist - create new user with random password random_password = GoogleOAuthService.generate_random_password() hashed_password = hash_password(random_password)
# Parse name into first and last name name_parts = google_name.split(" ", 1) first_name = name_parts[0] if name_parts else "User" last_name = name_parts[1] if len(name_parts) > 1 else ""
# Create user object user_create = UserCreate( first_name=first_name, last_name=last_name, email=google_email, password=hashed_password, phone="" # Default empty phone for OAuth users )
new_user, auth_message = await _create_new_user( db=db, user_in=user_create, send_verification_email=False # No email verification for OAuth users )
# Create tokens for new user tokens = create_token_pair( new_user.id, new_user.email, actor="user", ver="1", company_id="" ) auth_message = "Account created and logged in successfully via Google" user = new_user
# Set cookies response.set_cookie( key="refresh_token", value=tokens["refresh_token"], max_age=tokens['rt_expires_in'], httponly=True, secure=True, # only over HTTPS samesite="none" ) response.set_cookie( key="access_token", value=tokens["access_token"], max_age=tokens['at_expires_in'], httponly=True, secure=True, # only over HTTPS samesite="none" )
# Clear the state cookie response.delete_cookie(key="google_oauth_state") return DataResponse.success_response(data = TokenResponse(**tokens))
except Exception as e: return DataResponse.error_response(message=f"Google OAuth process failed: {str(e)}", status_code=status.HTTP_500_INTERNAL_SERVER_ERROR)
@router.post("/me/profile-photo", response_model=DataResponse[dict]) async def upload_profile_photo( *, db: AsyncSession = Depends(get_db), file: UploadFile = File(...), current_user: Users = Depends(get_current_active_user) ) -> DataResponse: """Upload profile photo to S3 and update user record.""" try: # Validate file type allowed_types = ["image/jpeg", "image/png", "image/jpg", "image/webp"] if file.content_type not in allowed_types: return DataResponse.error_response( message="Invalid file type. Only JPEG, PNG, and WebP are allowed", status_code=status.HTTP_400_BAD_REQUEST )
# Validate file size (e.g., max 5MB) file_content = await file.read() if len(file_content) > 5 * 1024 * 1024: return DataResponse.error_response( message="File size exceeds 5MB limit", status_code=status.HTTP_400_BAD_REQUEST )
# Upload to S3 (implement this service) from app.services.file_storage import file_storage_service photo_url = await file_storage_service.upload_file( file_content=file_content, file_name=f"users/{current_user.id}/profile.{file.filename.split('.')[-1]}", content_type=file.content_type )
# Update user record _ = await crud_user.update( db=db, db_obj=current_user, obj_in=UserUpdate(profile_photo_url=photo_url) )
return DataResponse.success_response( message="Profile photo uploaded successfully", data={"profile_photo_url": photo_url} ) except Exception as e: return DataResponse.error_response( message=f"Failed to upload profile photo: {str(e)}", status_code=status.HTTP_500_INTERNAL_SERVER_ERROR )
@router.delete("/me/profile-photo", response_model=DataResponse) async def delete_profile_photo( *, db: AsyncSession = Depends(get_db), current_user: Users = Depends(get_current_active_user) ) -> DataResponse: """Upload profile photo to S3 and update user record.""" try: # Update user record _ = await crud_user.update( db=db, db_obj=current_user, obj_in=UserUpdate(profile_photo_url=None) )
return DataResponse.success_response( message="Profile photo deleted successfully", ) except Exception as e: return DataResponse.error_response( message=f"Failed to delete profile photo: {str(e)}", status_code=status.HTTP_500_INTERNAL_SERVER_ERROR )

from fastapi import APIRouter, Depends, HTTPException, status, Response, Query, Request, File, UploadFile from sqlalchemy.ext.asyncio import AsyncSession from typing import List from datetime import datetime, timedelta import logging from fastapi.responses import RedirectResponse, JSONResponse
from app.api.dependencies import get_current_active_user, get_current_company_id from app.db.session import get_db from app.models import AvailabilityType from app.models.models import Users from app.schemas import User from app.schemas.auth import ( LoginRequest, TokenResponse, VerificationRequest, GoogleAuthorizationResponse, GoogleCallbackRequest, GoogleOAuthResponse ) from app.schemas.responses import DataResponse from app.schemas.schemas import ResponseMessage, TimeOffCreate, TimeOff, TimeOffUpdate, UserUpdate from app.schemas.schemas import UserCreate from app.services.auth import hash_password, verify_password, create_token_pair, verify_token from app.services.crud import user as crud_user from app.services.crud import user_time_off as crud_time_off from app.services.crud import company as crud_company from app.services.email_service import email_service, create_verification_token from app.services.google_oauth import GoogleOAuthService from app.models.enums import VerificationType, VerificationStatus
logger = logging.getLogger() router = APIRouter()
# Helper function to create a new user (reused by both signup and Google OAuth) async def _create_new_user( db: AsyncSession, user_in: UserCreate, send_verification_email: bool = True ) -> tuple[Users, str]: """ Internal helper to create a new user.
Returns: Tuple of (new_user, message) """ user_in.email = user_in.email.lower()
# Check if user already exists existing_user = await crud_user.get_by_email(db=db, email=user_in.email) if existing_user: raise ValueError("User with this email already exists")
# Hash password user_in.password = hash_password(user_in.password) new_user = await crud_user.create(db=db, obj_in=user_in)
# Create verification token verification_record = await create_verification_token( db=db, entity_id=new_user.id, verification_type=VerificationType.EMAIL, entity_type="user", expires_in_hours=24 )
if send_verification_email: # Send verification email try: user_name = f"{new_user.first_name} {new_user.last_name}" email_sent = email_service.send_verification_email( to_email=new_user.email, verification_token=verification_record.token, user_name=user_name ) except Exception as e: await db.rollback() logger.error(f"Error sending verification email: {str(e)}") email_sent = False
if not email_sent: raise Exception(f"Warning: Failed to send verification email to {new_user.email}")
message = "User created successfully. Please check your email to verify your account." else: # Auto-verify email for OAuth users await crud_user.verify_token(db=db, db_obj=verification_record) message = "User created successfully via Google OAuth"
return new_user, message
@router.post("/auth/signup", response_model=ResponseMessage, status_code=status.HTTP_201_CREATED) async def create_user( *, db: AsyncSession = Depends(get_db), response: Response, user_in: UserCreate ) -> ResponseMessage: """ Register a new user and send email verification. """
try: new_user, message = await _create_new_user(db, user_in, send_verification_email=True) response.status_code = status.HTTP_201_CREATED return ResponseMessage(message=message, status="success") except ValueError as e: response.status_code = status.HTTP_400_BAD_REQUEST return ResponseMessage(message=str(e), status="error") except Exception as e: await db.rollback() response.status_code = status.HTTP_500_INTERNAL_SERVER_ERROR return ResponseMessage(message=f"Internal server error: {str(e)}", status="error")
@router.post("/auth/verify-email", response_model=DataResponse) async def verify_email( *, db: AsyncSession = Depends(get_db), verification_in: VerificationRequest, response: Response ) -> DataResponse: """ Verify user email with token. """ try: token = await crud_user.get_verification_token( db=db, token=verification_in.token, type=VerificationType.EMAIL )
if not token: response.status_code = status.HTTP_404_NOT_FOUND return DataResponse.error_response( status_code=status.HTTP_404_NOT_FOUND, message="Verification token not found" )
if token.status != VerificationStatus.PENDING or token.expires_at < datetime.now(): response.status_code = status.HTTP_400_BAD_REQUEST return DataResponse.error_response( status_code=status.HTTP_400_BAD_REQUEST, message="Token has expired or is invalid" )
result = await crud_user.verify_token(db=db, db_obj=token) if result: return DataResponse.success_response( message="Email verified successfully" )
response.status_code = status.HTTP_400_BAD_REQUEST return DataResponse.error_response( message="Email verification failed", status_code=status.HTTP_400_BAD_REQUEST )
except Exception as e: response.status_code = status.HTTP_500_INTERNAL_SERVER_ERROR return DataResponse.error_response( message=f"Verification process failed: {str(e)}", status_code=status.HTTP_500_INTERNAL_SERVER_ERROR )
@router.post("/auth/login", response_model=DataResponse[TokenResponse]) async def user_login( login_data: LoginRequest, response: Response, db: AsyncSession = Depends(get_db) ) -> DataResponse: """ Login professional using mobile number or email and return JWT tokens. """
# Try to get professional by mobile number first user = await crud_user.get_by_email(db, email=login_data.email)
if not user: raise HTTPException( status_code=status.HTTP_401_UNAUTHORIZED, detail="Invalid credentials" )
# Verify password if not verify_password(login_data.password, user.password): raise HTTPException( status_code=status.HTTP_401_UNAUTHORIZED, detail="Invalid credentials" ) company = await crud_user.get_company_by_user(db, user.id)
# Create token pair tokens = create_token_pair(user.id, user.email, actor="user", ver="1", company_id=str(company.company_id) if company else '')
response.set_cookie( key="refresh_token", value=tokens["refresh_token"], max_age=tokens['rt_expires_in'], httponly=True, secure=True, # only over HTTPS samesite="none" ) response.set_cookie( key="access_token", value=tokens["access_token"], max_age=tokens['at_expires_in'], httponly=True, secure=True, # only over HTTPS samesite="none" )
return DataResponse.success_response(data = TokenResponse(**tokens))
@router.put("/auth/logout") async def logout_user(response: Response): """ Logout professional by clearing the refresh token cookie """ from app.core.config import settings try: # Determine cookie domain - must match the domain used when setting cookies cookie_domain = ".salona.me" if "salona.me" in settings.API_URL else None is_production = "https://" in settings.API_URL
response.set_cookie( key="refresh_token", value='', max_age=0, httponly=True, secure=True, # only over HTTPS samesite="none" ) response.set_cookie( key="access_token", value='', max_age=0, httponly=True, secure=True, # only over HTTPS samesite="none" )
return ResponseMessage(message="Logged out successfully", status="success") except Exception as e: response.status_code = status.HTTP_500_INTERNAL_SERVER_ERROR return ResponseMessage(message=f"Internal server error: {str(e)}", status="error")
