from pydantic.v1 import UUID4 from sqlalchemy.orm import Session from sqlalchemy import func
from app.models import Bookings from app.models.models import (Customers, CustomerVerifications, CustomerEmails) from app.models.enums import VerificationStatus, BookingStatus from app.schemas.schemas import ( CustomerCreate, CustomerUpdate, CompanyCustomer ) from app.core.datetime_utils import utcnow
