def get_company_customers(db: Session, company_id: str) -> List[CompanyCustomer]: # Get all unique customers who have bookings with this company customers = ( db.query(Customers) .join(Bookings, Customers.id == Bookings.customer_id) .filter(Bookings.company_id == company_id) .distinct() .all() )
