    availabilities: List[UserAvailabilities],
    time_offs: List[UserTimeOffs],
    bookings: List[Any],
    availability_type: AvailabilityType,
    date_from: date,
    service_duration_minutes: Optional[int] = None
) -> AvailabilityResponse:
